mapper.insert:
INSERT INTO 
	tb_qna_board (
		qna_seq
		, up_seq
		, reg_dt
		, title
		, content
		, reg_sts
		, reg_dtm
		, reg_id
	)
	VALUES (
		#{qna_seq}
		, #{up_seq}
		, DATE_FORMAT(#{reg_dtm},'%Y%m%d')
		, #{title}
		, #{content}
		, #{reg_sts}
		, NOW()
		, #{user_id}
	)

mapper.update:
UPDATE tb_qna_board
SET 
	title = #{title}
	,content = #{content}
	,reg_sts = #{reg_sts}
	,upd_dtm = NOW()
	,upd_id = #{upd_id}
WHERE 
	qna_seq = #{qna_seq};
