mapper.select:
SELECT COUNT(*)
FROM tb_member
WHERE user_id = #{user_id}

mapper.insert:
INSERT INTO tb_member
	(
		user_id
		, mem_gbn
		, han_name
		, eng_name
		, tel_no
		, email
		, passwd
		, ret_yn
		, reg_dtm
		, upd_dtm
	)
VALUES
	(
		#{user_id}
		, "02"
		, #{han_name}
		, #{eng_name}
		, #{tel_no}
		, #{email}
		, #{passwd}
		, "N"
		, #{reg_dtm}
		, #{upd_dtm}
	)

mapper.select:
SELECT user_id
FROM tb_member
WHERE han_name	= #{han_name}
AND tel_no		= #{tel_no}
AND email		= #{email}

SELECT passwd
FROM tb_member
WHERE user_id	= #{user_id}
AND tel_no		= #{tel_no}
AND email		= #{email}

mapper.update:
UPDATE tb_member
SET ret_yn = "R" , passwd = #{passwd}
WHERE user_id = #{user_id}

UPDATE
	tb_member
SET 
	email		=	#{email}
	, passwd	=	#{passwd}
	, upd_dtm	=	#{upd_dtm}
	, ret_yn	=	"N"
WHERE
	user_id = #{user_id}
