mapper.insert:
        INSERT INTO doollee_simpc (
	          simp_tpc
	         ,simp_tpcnm
	         ,simp_c
	         ,simp_cnm
	         ,use_yn
	         ,order_num
	         ,attr_1
	         ,attr_2
	         ) VALUES (
	          #{simp_tpc}
	         ,#{simp_tpcnm}
	         ,#{simp_c}
	         ,#{simp_cnm}
	         ,#{use_yn}
	         ,#{order_num}
	         ,#{attr_1}
	         ,#{attr_2}
	   )
	   ON
	      DUPLICATE KEY
	   UPDATE
	        simp_tpcnm   = #{simp_tpcnm}
	      , simp_cnm     = #{simp_cnm}
	      , use_yn       = #{use_yn}
	      , order_num    = #{order_num}
	      , attr_1       = #{attr_1}
	      , attr_2       = #{attr_2}

mapper.update:
        UPDATE doollee_simpc
           SET simp_tpcnm   = #{simp_tpcnm}
             , simp_cnm     = #{simp_cnm}
             , use_yn       = #{use_yn}
             , order_num    = #{order_num}
             , attr_1       = #{attr_1}
	         , attr_2       = #{attr_2}
         WHERE simp_tpc = #{simp_tpc}
           AND simp_c   = #{simp_c}
